$.headerMove = function(a) {
	$(".navList").mouseover(function() {
		$(".header").css('overflow','visible');
		$(".header").stop().animate({
			height: a
		}, {
			easing: "easeOutExpo",
			duration: 700
		});
		$(".navList").stop().animate({
			height: a
		}, {
			easing: "easeOutExpo",
			duration: 700
		});
		$(".list-menu").stop().animate({
			height: a
		}, {
			easing: "easeOutExpo",
			duration: 700
		})
	});
	$(".navList").mouseleave(function() {
		$(".header").css('overflow','visible');
		$(".header").stop().animate({
			height: "100px"
		}, {
			easing: "easeOutExpo",
			duration: 700
		});
		$(".navList").stop().animate({
			height: "100px"
		}, {
			easing: "easeOutExpo",
			duration: 700
		});
		$(".list-menu").stop().animate({
			height: "100px"
		}, {
			easing: "easeOutExpo",
			duration: 700
		})
	});
	$(".header").css('overflow','visible');
	$(".list-menu").mouseover(function() {
		$(this).addClass("onBg").siblings(".list-menu").removeClass("onBg");
		$(this).children(".one-menu").addClass("red").parents(".list-menu").siblings(".list-menu").children("a").removeClass("red")
	});
	$(".list-menu").mouseout(function() {
		$(this).removeClass("onBg");
		$(".one-menu").removeClass("red")
	})
};
