//QQ弹层
$(function() {
	$(".fixMenu .onlineQQ").click(function() {
		var a = $(".fixMenu .onlineQQ").offset().left,
			d = $(".fixMenu .onlineQQ").offset().top - $(window).scrollTop(),
			e = $(".alert_QQservice .QQservice_box").width(),
			g = $(".alert_QQservice .QQservice_box").height() - 20,
			c = ($(window).width() - e) / 2,
			b = ($(window).height() - g) / 2;
		$("body").append('<div class="mask"></div>');
		$(".mask").css({
			width: $(window).width(),
			height: $(document).height(),
			"margin-left": -$(window).width() / 2,
			"margin-top": -$(window).height() / 2
		}).show(300);
		$(".alert_QQservice").css({
			left: a,
			top: d
		});
		$(".alert_QQservice").animate({
			width: e,
			height: g,
			left: c,
			top: b
		}, 300, function() {
			$(".alert_QQservice").css("border", "2px solid #fff")
		});
		$(".mask").click(function() {
			$(".alert_QQservice .closeA").trigger("click")
		})
	});
	$(".alert_QQservice .closeA").click(function() {
		var a = $(".fixMenu .onlineQQ").offset().left,
			d = $(".fixMenu .onlineQQ").offset().top - $(window).scrollTop();
		$(".alert_QQservice .QQservice_box").width();
		$(".alert_QQservice .QQservice_box").height();
		$(window).width();
		$(window).height();
		$(".mask").hide(300, function() {
			$(".mask").remove()
		});
		$(".alert_QQservice").animate({
			width: "0",
			height: "0",
			left: a,
			top: d
		}, 300, function() {
			$(".alert_QQservice").css("border", "0")
		})
	});
	$("#click_consult").click(function() {
		$(".fixMenu .onlineQQ").trigger("click")
	})
});
