//弹层公用方法
$(function(){
	function alertBox(ele){
		var eleL = -$(ele).outerWidth()/2;
		var eleT = -$(ele).outerHeight()/2;
		var winH = $(window).height();
		var winW = $(window).width();
		var maskT = -$(window).height()/2;
		var maskL = -$(window).width()/2;
		$(ele).addClass('alertBox').css({'margin-left':eleL,'margin-top':eleT}).show(300);
		$('body').append("<div class='mask'></div>");
		$('.mask').css({
			'width':winW,
			'height':winH,
			'margin-left': maskL,
			'margin-top': maskT}).show(300);
		};
	function hideBox(ele){
		$(ele).hide(300);
		$('.mask').hide(300,function(){$('.mask').remove();});
		};	
	$('.topNav ul .loginLi a:first').click(function(){
		$('.newUser .nav-tabs li:first').trigger('click')
		.addClass('active').siblings().removeClass('active');
		alertBox('.newUser');
		});	
		$('.newUser .close_btn').click(function(){
			hideBox('.newUser');
			});
	$('.topNav ul .loginLi a:last').click(function(){
		$('.newUser .nav-tabs li:last').trigger('click')
		.addClass('active').siblings().removeClass('active');
		alertBox('.newUser');
		});	
		$('.newUser .close_btn').click(function(){
			hideBox('.newUser');
			});	
	});
